import React from 'react';
import {generateResult} from '../../firebase/dataHandling'
import ReviewCard from '../../components/ReviewCard/ReviewCard';
import firebase from 'firebase/app'
import PageHeader from '../../components/pageHeader/PageHeader'
import ObservationDialog from '../../components/ObservationDialog/ObservationDialog'
class Pending extends React.Component {
  constructor(props){
    super(props)
    this.state={
      observations: [],
      month: [],
      showModal: false,
    }
  }
  componentDidMount(){
    firebase.database().ref("users").on("value", snapshot => {
      const result = snapshot.val()
      let observations = []
      for(let i in result){
        let user = result[i].name
        let userPhoto = result[i].photo
        let userObs = result[i].observations
        for(let j in userObs){
          //console.log(userObs[j])
          if(userObs[j].verified){continue}
          let obPhoto = userObs[j].photoURL
          let time = new Date(userObs[j].time)

          time = time.toString().split(" ")
          this.state.month.push(time[1])
          time = time.splice(0, time.length - 4)
          time = time.toString().replace(/,/g, ' ')

          let result = generateResult(userObs[j])
          observations.push([user,userPhoto,obPhoto, time, result, j, i])
          this.setState({observations: observations})
        }
      }

    })
  }

  reviewCard = (child)=>{
    console.log(child)
    this.setState({
      showModal: child
    })
  }
  handleClose = () => {
    this.setState({
      showModal: false
    })

  };
  render(){
    return (
      <div style={{marginTop:60, width: '100%' }}>
          <PageHeader title={'Pending Observations'} subtitle={''}/>
          <div style={{ display: 'inline-flex', flexWrap: 'wrap', justifyContent: 'center', alignItems: 'center' }}>
            {this.state.observations.map((ob,i)=>{
              console.log(ob[3].split(" ")[1])
              let component = []
              if(i===0 || this.state.observations[i-1][3].split(" ")[1]!==ob[3].split(" ")[1]){
                component.push( <h2 key={i+this.state.observations.length} style={{ display: 'block', width: "100%"}}>{ob[3].split(" ")[3]+" "+ob[3].split(" ")[1]}</h2>)

              }
              component.push(<ReviewCard 
                              verified={false} 
                              userId={ob[6]} 
                              id={ob[5]} 
                              user={ob[0]} 
                              userPhoto={ob[1]} 
                              obPhoto={ob[2]} 
                              time={ob[3]} 
                              result={ob[4]}  
                              key={i}
                              parentCallback={this.reviewCard}
                            />)
              return component
            })}
            <ObservationDialog 
              userPhoto={'props.userPhoto'} 
              user={'props.user'} 
              time={'props.time'} 
              verified={'props.verified'} 
              userId={'props.userId'} 
              id={'props.id'} 
              img={'props.obPhoto'} 
              result={[[]]} 
              selectedValue={'selectedValue'} 
              open={this.state.showModal} 
              onClose={this.handleClose} 
              />
          </div>

      </div>
    );
  }

};

export default Pending;
