import React from 'react';
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Avatar from '@material-ui/core/Avatar';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import ListItemText from '@material-ui/core/ListItemText';
import DialogTitle from '@material-ui/core/DialogTitle';
import Dialog from '@material-ui/core/Dialog';
import { blue } from '@material-ui/core/colors';
import Icon from '@material-ui/core/Icon';
import Swal from 'sweetalert2'
var firebase = require("firebase");

const useStyles = makeStyles({
  avatar: {
    backgroundColor: blue[100],
    color: blue[600],
  },
});

export default function SimpleDialog(props) {
  const classes = useStyles();
  const { onClose, selectedValue, open } = props;

  const handleClose = () => {
    onClose(selectedValue);
  };

  const handleListItemClick = value => {
    onClose(value);
  };

  const verifyHandler = (id, userId) => {
    firebase.database().ref().child('users').child(userId).child('observations').child(id).child('verified').set(true)
    handleClose()
    console.log(id)
  }

  const deleteHandler = (id, userId) => {
    handleClose()
    Swal.fire({
      title: 'Are you sure?',
      text: "You won't be able to revert this!",
      icon: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#3085d6',
      cancelButtonColor: '#d33',
      confirmButtonText: 'Yes, delete it!'
    }).then((result) => {
      if (result.value) {
        Swal.fire(
          'Deleted!',
          'Your file has been deleted.',
          'success'
        )
      }
    })
  }

  return (
    <Dialog onClose={handleClose} aria-labelledby="simple-dialog-title" open={open}>
      <DialogTitle id="simple-dialog-title">Observation
        <Button variant="outlined" onClick={()=>verifyHandler(props.id, props.userId)} color="primary">Verify</Button>
        <Button variant="outlined" onClick={()=>deleteHandler(props.id, props.userId)} color="secondary">Reject</Button>
      </DialogTitle>
        <img alt={'scorelab.org'} style={{height: 300}} src={props.img}/> 
        <List>
            {props.result.map(item => (
            <ListItem button onClick={() => handleListItemClick(item[1])} key={item[1]}>
                <ListItemAvatar>
                <Avatar className={classes.avatar}>
                    <Icon color="primary">add_circle</Icon>
                </Avatar>
                </ListItemAvatar>
                <ListItemText primary={item[1]} />
            </ListItem>
            ))}
        </List>

    </Dialog>
  );
}

SimpleDialog.propTypes = {
  onClose: PropTypes.func.isRequired,
  open: PropTypes.bool.isRequired,
  selectedValue: PropTypes.string.isRequired,
};
